-- Name: agenteinsupd(character varying, bigint, bigint, character varying, character varying, character varying, character varying, character varying, character varying, character varying, character varying, character varying, character varying, character varying, character varying, character varying, character varying, character varying, character varying); Type: FUNCTION; Schema: promogest; Owner: promoadmin
--

CREATE FUNCTION agenteinsupd(character varying, bigint, bigint, character varying, character varying, character varying, character varying, character varying, character varying, character varying, character varying, character varying, character varying, character varying, character varying, character varying, character varying, character varying, character varying) RETURNS resultid
    AS $_$

    DECLARE
        -- Parametri contesto
        _schema                     ALIAS FOR $1;
        _idutente                   ALIAS FOR $2;

        --Parametri tabella
        _id                         ALIAS FOR $3;
        _codice                     ALIAS FOR $4;
        _ragione_sociale            ALIAS FOR $5;
        _insegna                    ALIAS FOR $6;
        _cognome                    ALIAS FOR $7;
        _nome                       ALIAS FOR $8;
        _sede_operativa_indirizzo   ALIAS FOR $9;
        _sede_operativa_cap         ALIAS FOR $10;
        _sede_operativa_provincia   ALIAS FOR $11;
        _sede_operativa_localita    ALIAS FOR $12;
        _sede_legale_indirizzo      ALIAS FOR $13;
        _sede_legale_cap            ALIAS FOR $14;
        _sede_legale_provincia      ALIAS FOR $15;
        _sede_legale_localita       ALIAS FOR $16;
        _nazione                    ALIAS FOR $17;
        _codice_fiscale             ALIAS FOR $18;
        _partita_iva                ALIAS FOR $19;

        sql_command                 varchar(2000);
        schema_prec                 varchar(2000);
        logid                       bigint;
        _resultid                   promogest.resultid;
        TempId                      bigint;
    BEGIN
        IF _id IS NULL THEN
            INSERT INTO persona_giuridica (codice,ragione_sociale,insegna,cognome,nome,sede_operativa_indirizzo,sede_operativa_cap,sede_operativa_provincia,sede_operativa_localita,sede_legale_indirizzo,sede_legale_cap,sede_legale_provincia,sede_legale_localita,nazione,codice_fiscale,partita_iva) 
                VALUES (_codice,_ragione_sociale,_insegna,_cognome,_nome,_sede_operativa_indirizzo,_sede_operativa_cap,_sede_operativa_provincia,_sede_operativa_localita,_sede_legale_indirizzo,_sede_legale_cap,_sede_legale_provincia,_sede_legale_localita,_nazione,_codice_fiscale,_partita_iva); 
                
            TempId := CURRVAL('persona_giuridica_id_seq');

            INSERT INTO agente(id) 
                VALUES (TempId);
                
            PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.AgenteInsUpd','Inserito Agente',NULL,TempId);
            SELECT INTO _resultid TempId;
        ELSE
            UPDATE persona_giuridica SET
                 codice = _codice
                ,ragione_sociale = _ragione_sociale
                ,insegna = _insegna
                ,cognome = COALESCE(_cognome,cognome)
                ,nome = COALESCE(_nome,nome)
                ,sede_operativa_indirizzo = _sede_operativa_indirizzo
                ,sede_operativa_cap = _sede_operativa_cap
                ,sede_operativa_provincia = _sede_operativa_provincia
                ,sede_operativa_localita = _sede_operativa_localita
                ,sede_legale_indirizzo = _sede_legale_indirizzo
                ,sede_legale_cap = _sede_legale_cap
                ,sede_legale_provincia = _sede_legale_provincia
                ,sede_legale_localita = _sede_legale_localita
                ,nazione = _nazione
                ,codice_fiscale = _codice_fiscale
                ,partita_iva = _partita_iva
            WHERE id = _id;

            IF FOUND THEN
                PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.AgenteInsUpd','Modificato Agente',_ragione_sociale,_id);
                SELECT INTO _resultid _id;
            ELSE
                PERFORM promogest.LogSet(_idutente, _schema,  'E','promogest.AgenteInsUpd','Agente non trovato',_ragione_sociale,_id);
                RAISE WARNING 'Agente non trovato: %',_id;
                logid := CURRVAL('promogest.application_log_id_seq');
                SELECT INTO _resultid -logid;
            END IF;
        END IF; 
        RETURN _resultid;
    END;
$_$
    LANGUAGE plpgsql;


