--

CREATE FUNCTION aliquotaivainsupd(character varying, bigint, bigint, character varying, character varying, numeric, numeric, character varying, bigint) RETURNS resultid
    AS $_$
    DECLARE
        -- Parametri contesto
        _schema                 ALIAS FOR $1;
        _idutente               ALIAS FOR $2;
        
        -- Parametri tabella
        _id                     ALIAS FOR $3;
        _denominazione_breve    ALIAS FOR $4;
        _denominazione          ALIAS FOR $5;
        _percentuale            ALIAS FOR $6;
        _percentuale_detrazione ALIAS FOR $7;
        _descrizione_detrazione ALIAS FOR $8;
        _id_tipo                ALIAS FOR $9;
        
        sql_command             varchar(2000);
        schema_prec             varchar(2000);
        logid                   bigint;
        _resultid               promogest.resultid;
        TempId                  bigint;
    BEGIN
        IF _id IS NULL THEN
            INSERT INTO aliquota_iva (denominazione, denominazione_breve, percentuale, percentuale_detrazione, descrizione_detrazione, id_tipo) 
                VALUES (_denominazione, _denominazione_breve, _percentuale, _percentuale_detrazione, _descrizione_detrazione, _id_tipo);
                
            TempId := CURRVAL('aliquota_iva_id_seq');
            PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.AliquotaIvaInsUpd','Inserita aliquota_iva',NULL,TempId);
            SELECT INTO _resultid TempId;
        ELSE
            UPDATE aliquota_iva SET
                 denominazione = COALESCE(_denominazione,denominazione)
                ,denominazione_breve = COALESCE(_denominazione_breve,denominazione_breve)
                ,percentuale = COALESCE(_percentuale,percentuale)
                ,percentuale_detrazione = _percentuale_detrazione
                ,descrizione_detrazione = _descrizione_detrazione
                ,id_tipo = COALESCE(_id_tipo,id_tipo)
            WHERE id = _id;

            IF FOUND THEN
                PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.AliquotaIvaInsUpd','Modificata aliquota_iva',NULL,_id);
                SELECT INTO _resultid _id;
            ELSE
                PERFORM promogest.LogSet(_idutente, _schema,  'E','promogest.AliquotaIvaInsUpd','AliquotaIva non trovata',NULL,_id);
                RAISE WARNING 'AliquotaIva non trovata: %',_id;
                logid := CURRVAL('promogest.application_log_id_seq');
                SELECT INTO _resultid -logid;
            END IF;
        END IF; 
        RETURN _resultid;
    END;
$_$
    LANGUAGE plpgsql;


