--

CREATE FUNCTION contattoclienteinsupd(character varying, bigint, bigint, character varying, character varying, character varying, character varying, character varying, text, bigint) RETURNS resultid
    AS $_$
    DECLARE
        -- Parametri contesto
        _schema             ALIAS FOR $1;
        _idutente           ALIAS FOR $2;
        
        -- Parametri tabella
        _id                 ALIAS FOR $3;
        _tipo_contatto      ALIAS FOR $4;
        _nome               ALIAS FOR $5;
        _cognome            ALIAS FOR $6;
        _ruolo              ALIAS FOR $7;
        _descrizione        ALIAS FOR $8;
        _note               ALIAS FOR $9;
        _id_cliente         ALIAS FOR $10;
        
        tipo                varchar(50);
        schema_prec         varchar(2000);
        logid               bigint;
        _resultid           promogest.resultid;
        TempId              bigint;
    BEGIN
        tipo := 'cliente';
        IF _id IS NULL THEN
            INSERT INTO contatto (tipo_contatto, nome,cognome,ruolo,descrizione,note) 
                VALUES (tipo, _nome,_cognome,_ruolo,_descrizione,_note);
                
            TempId := CURRVAL('contatto_id_seq');

            INSERT INTO contatto_cliente (id, tipo_contatto, id_cliente) 
                VALUES (TempId, tipo, _id_cliente);
                
            PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.ContattoClienteInsUpd','Inserito contatto magazzino',NULL,TempId);
            SELECT INTO _resultid TempId;
        ELSE
            UPDATE contatto SET
                 nome = COALESCE(_nome,nome)
                ,cognome = COALESCE(_cognome,cognome)
                ,ruolo = _ruolo
                ,descrizione = _descrizione
                ,note = _note
            WHERE id = _id;

            UPDATE contatto_cliente SET
                id_cliente = COALESCE(_id_cliente,id_cliente) 
            WHERE id = _id;

            IF FOUND THEN
                PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.ContattoClienteInsUpd','Modificato contatto cliente',NULL,_id);
                SELECT INTO _resultid _id;
            ELSE
                PERFORM promogest.LogSet(_idutente, _schema,  'E','promogest.ContattoClienteInsUpd','Contatto cliente non trovato',NULL,_id);
                RAISE WARNING 'Contatto cliente non trovato: %',_id;
                logid := CURRVAL('promogest.application_log_id_seq');
                SELECT INTO _resultid -logid;
            END IF;
        END IF;
        RETURN _resultid;
    END;
$_$
    LANGUAGE plpgsql;


