            INSERT INTO contatto (tipo_contatto,nome,cognome,ruolo,descrizione,note) 
                VALUES (tipo,_nome,_cognome,_ruolo,_descrizione,_note);
                
            TempId := CURRVAL('contatto_id_seq');

            INSERT INTO contatto_fornitore (id, tipo_contatto, id_fornitore) 
                VALUES (TempId, tipo, _id_fornitore);
                
            PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.ContattoFornitoreInsUpd','Inserito contatto fornitore',NULL,TempId);
