                VALUES (tipo,_nome,_cognome,_ruolo,_descrizione,_note);
                
            TempId := CURRVAL('contatto_id_seq');

            INSERT INTO contatto_magazzino (id, tipo_contatto, id_magazzino) 
                VALUES (TempId, tipo, _id_magazzino);
                
            PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.ContattoMagazzinoInsUpd','Inserito contatto magazzino',NULL,TempId);
