        _partita_iva                ALIAS FOR $19;
        _id_categoria               ALIAS FOR $20;
        _id_pagamento               ALIAS FOR $21;
        _id_magazzino               ALIAS FOR $22;

        sql_command                 varchar(2000);
        schema_prec                 varchar(2000);
        logid                       bigint;
        _resultid                   promogest.resultid;
        TempId                      bigint;
    BEGIN
        IF _id IS NULL THEN
            INSERT INTO persona_giuridica (codice,ragione_sociale,insegna,cognome,nome,sede_operativa_indirizzo,sede_operativa_cap,sede_operativa_provincia,sede_operativa_localita,sede_legale_indirizzo,sede_legale_cap,sede_legale_provincia,sede_legale_localita,nazione,codice_fiscale,partita_iva) 
