            TempId := CURRVAL('persona_giuridica_id_seq');
                
            INSERT INTO fornitore ( id, id_categoria, id_pagamento, id_magazzino ) 
                VALUES ( TempId, _id_categoria, _id_pagamento, _id_magazzino );
                
            PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.FornitoreInsUpd','Inserito Fornitore',NULL,TempId);
            SELECT INTO _resultid TempId;
        ELSE
            UPDATE persona_giuridica SET
                 codice = _codice
                ,ragione_sociale = _ragione_sociale
                ,insegna = _insegna
                ,cognome = COALESCE(_cognome,cognome)
                ,nome = COALESCE(_nome,nome)
                ,sede_operativa_indirizzo = _sede_operativa_indirizzo
                ,sede_operativa_cap = _sede_operativa_cap
