                ,nazione = _nazione
                ,codice_fiscale = _codice_fiscale
                ,partita_iva = _partita_iva
            WHERE id = _id;

            UPDATE fornitore SET
                 id_categoria = _id_categoria
                ,id_pagamento = _id_pagamento
                ,id_magazzino = _id_magazzino
            WHERE id = _id;

            IF FOUND THEN
                PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.FornitoreInsUpd','Modificato Fornitore',_ragione_sociale,_id);
