        IF _orderby IS NULL THEN
            OrderBy = 'ragione_sociale ';
        ELSE
            OrderBy = _orderby;
        END IF;
        
        IF (_codice IS NULL AND _ragione_sociale IS NULL AND
            _insegna IS NULL AND _nome IS NULL AND
            _localita IS NULL AND _partita_iva IS NULL AND
            _codice_fiscale IS NULL AND
            _id_categoria_fornitore IS NULL) THEN
