--

CREATE FUNCTION forniturainsupd(character varying, bigint, bigint, character varying, numeric, numeric, character varying, integer, integer, boolean, numeric, date, date, bigint, bigint, bigint) RETURNS resultid
    AS $_$

    DECLARE
        -- Parametri contesto
        _schema                     ALIAS FOR $1;
        _idutente                   ALIAS FOR $2;

        --Parametri tabella
        _id                         ALIAS FOR $3;
        _codice_articolo_fornitore  ALIAS FOR $4;
        _prezzo_lordo               ALIAS FOR $5;
        _prezzo_netto               ALIAS FOR $6;
        _applicazione_sconti        ALIAS FOR $7;
        _scorta_minima              ALIAS FOR $8;
        _tempo_arrivo_merce         ALIAS FOR $9;
        _fornitore_preferenziale    ALIAS FOR $10;
        _percentuale_iva            ALIAS FOR $11;
        _data_fornitura             ALIAS FOR $12;
        _data_prezzo                ALIAS FOR $13;
        _id_fornitore               ALIAS FOR $14;
        _id_articolo                ALIAS FOR $15;
        _id_multiplo                ALIAS FOR $16;

        sql_command                 varchar(2000);
        schema_prec                 varchar(2000);
        logid                       bigint;
        _resultid                   promogest.resultid;
        TempId                      bigint;
        _cnt                        bigint;
        __fornitore_preferenziale   boolean;
    BEGIN
        IF _id IS NULL THEN
            SELECT INTO __fornitore_preferenziale _fornitore_preferenziale;
            IF _fornitore_preferenziale IS NULL THEN
                SELECT INTO _cnt COUNT(*) 
                    FROM fornitura 
                    WHERE id_articolo = _id_articolo;
        
                IF _cnt = 0 THEN
                    -- prima fornitura in assoluto
                    SELECT INTO __fornitore_preferenziale 't';
                ELSE
                    SELECT INTO __fornitore_preferenziale 'f';
                END IF;
            END IF;

            INSERT INTO fornitura (codice_articolo_fornitore, prezzo_lordo, prezzo_netto, applicazione_sconti, scorta_minima, tempo_arrivo_merce, fornitore_preferenziale, percentuale_iva, data_fornitura, data_prezzo, id_fornitore, id_articolo, id_multiplo) 
                VALUES (_codice_articolo_fornitore, _prezzo_lordo, _prezzo_netto, _applicazione_sconti, _scorta_minima, _tempo_arrivo_merce, __fornitore_preferenziale, _percentuale_iva, _data_fornitura, _data_prezzo, _id_fornitore, _id_articolo, _id_multiplo);
                
            TempId := CURRVAL('fornitura_id_seq');
            PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.FornituraInsUpd','Inserita Fornitura',NULL,TempId);
            SELECT INTO _resultid TempId;
        ELSE
            UPDATE fornitura SET
                 codice_articolo_fornitore = _codice_articolo_fornitore
                ,prezzo_lordo = COALESCE(_prezzo_lordo,prezzo_lordo) 
                ,prezzo_netto = COALESCE(_prezzo_netto,prezzo_netto) 
                ,applicazione_sconti = COALESCE(_applicazione_sconti,applicazione_sconti) 
                ,scorta_minima = _scorta_minima
                ,tempo_arrivo_merce = _tempo_arrivo_merce
                ,fornitore_preferenziale = COALESCE(_fornitore_preferenziale,fornitore_preferenziale)
                ,percentuale_iva = _percentuale_iva
                ,data_fornitura = _data_fornitura
                ,data_prezzo = _data_prezzo
                ,id_fornitore = COALESCE(_id_fornitore,id_fornitore)
                ,id_articolo = COALESCE(_id_articolo,id_articolo) 
                ,id_multiplo = _id_multiplo
            WHERE id = _id;

            IF FOUND THEN
                PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.FornituraInsUpd','Modificata Fornitura',NULL,_id);
                SELECT INTO _resultid _id;
            ELSE
                PERFORM promogest.LogSet(_idutente, _schema,  'E','promogest.FornituraInsUpd','Fornitura non trovata',NULL,_id);
                RAISE WARNING 'Fornitura non trovata: %',_id;
                logid := CURRVAL('promogest.application_log_id_seq');
                SELECT INTO _resultid -logid;
            END IF;
        END IF; 
        RETURN _resultid;
    END;
$_$
    LANGUAGE plpgsql;


