CREATE FUNCTION inventarioinsupd(character varying, bigint, bigint, integer, bigint, bigint, numeric, numeric, date) RETURNS resultid
    AS $_$

    DECLARE
        -- Parametri contesto
        _schema                     ALIAS FOR $1;
        _idutente                   ALIAS FOR $2;

        --Parametri tabella
        _id                         ALIAS FOR $3;
        _anno                       ALIAS FOR $4;
        _id_magazzino               ALIAS FOR $5;
        _id_articolo                ALIAS FOR $6;
        _quantita                   ALIAS FOR $7;
        _valore_unitario            ALIAS FOR $8;
        _data_aggiornamento         ALIAS FOR $9;

        sql_command                 varchar(2000);
        schema_prec                 varchar(2000);
        logid                       bigint;
        _resultid                   promogest.resultid;
        TempId                      bigint;
    BEGIN
        IF _id IS NULL THEN
            INSERT INTO inventario (id, anno, id_magazzino, id_articolo, quantita, valore_unitario, data_aggiornamento) 
                VALUES (_id, _anno, _id_magazzino, _id_articolo, _quantita, _valore_unitario, _data_aggiornamento);
                
            TempId := CURRVAL('inventario_id_seq');
            PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.InventarioInsUpd','Inserito inventario',NULL,TempId);
            SELECT INTO _resultid TempId;
        ELSE
            UPDATE inventario SET
                 id_magazzino = COALESCE(_id_magazzino,id_magazzino)
                ,id_articolo = COALESCE(_id_articolo,id_articolo)
                ,quantita = _quantita
                ,valore_unitario = _valore_unitario
                ,data_aggiornamento = data_aggiornamento
            WHERE id = _id;

            IF FOUND THEN
                PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.InventarioInsUpd','Modificato inventario',NULL,_id);
