--

CREATE FUNCTION listinoinsupd(character varying, bigint, bigint, character varying, character varying, date, boolean) RETURNS resultid
    AS $_$
    DECLARE
        -- Parametri contesto
        _schema                 ALIAS FOR $1;
        _idutente               ALIAS FOR $2;

        --Parametri tabella
        _id                     ALIAS FOR $3;
        _denominazione          ALIAS FOR $4;       
        _descrizione            ALIAS FOR $5;
        _data_listino           ALIAS FOR $6;
        _listino_attuale        ALIAS FOR $7;
                        
        sql_command             varchar(2000);
        schema_prec             varchar(2000);
        logid                   bigint;
        _resultid               promogest.resultid;
        TempId                  bigint;
        _cnt                    bigint;
    BEGIN
        IF _id IS NULL THEN
            -- Verifico se ci sono listini attuali con la stessa denominazione
            SELECT INTO _cnt COUNT(*) 
                FROM listino WHERE denominazione = _denominazione AND listino_attuale;
                
            IF _cnt > 0 THEN
                UPDATE listino 
                    SET listino_attuale = 'f' WHERE denominazione = _denominazione;
            END IF;
        
            INSERT INTO listino (denominazione, descrizione, data_listino, listino_attuale)
                    VALUES (_denominazione, _descrizione, _data_listino, 't');
                    
            TempId := CURRVAL('listino_id_seq');
            PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.ListinoUpd','Inserito listino',NULL,TempId);
            SELECT INTO _resultid TempId;
        ELSE
            UPDATE listino SET
                 denominazione = COALESCE(_denominazione,denominazione)
                ,descrizione = COALESCE(_descrizione,descrizione)
                ,data_listino = COALESCE(_data_listino,data_listino)
            WHERE id = _id;

            IF FOUND THEN
                PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.ListinoInsUpd','Modificato listino',NULL,_id);
                SELECT INTO _resultid _id;
            ELSE
                PERFORM promogest.LogSet(_idutente, _schema,  'E','promogest.ListinoInsUpd','Listino non trovato',NULL,_id);
                RAISE WARNING 'Listino non trovato: %',_id;
                logid := CURRVAL('promogest.application_log_id_seq');
                SELECT INTO _resultid -logid;
            END IF;
        END IF;
        RETURN _resultid;
    END;
$_$
    LANGUAGE plpgsql;


