-- Name: magazzinoinsupd(character varying, bigint, bigint, character varying, character varying, character varying, character varying, character varying, character varying, timestamp without time zone); Type: FUNCTION; Schema: promogest; Owner: promoadmin
--

CREATE FUNCTION magazzinoinsupd(character varying, bigint, bigint, character varying, character varying, character varying, character varying, character varying, character varying, timestamp without time zone) RETURNS resultid
    AS $_$
    DECLARE
        -- Parametri contesto
        _schema                         ALIAS FOR $1;
        _idutente                       ALIAS FOR $2;
        
        -- Parametri tabella
        _id                             ALIAS FOR $3;
        _denominazione                  ALIAS FOR $4;
        _indirizzo                      ALIAS FOR $5;
        _localita                       ALIAS FOR $6;
        _cap                            ALIAS FOR $7;
        _provincia                      ALIAS FOR $8;
        _nazione                        ALIAS FOR $9;
        _data_ultima_stampa_giornale    ALIAS FOR $10;
        
        sql_command                     varchar(2000);
        schema_prec                     varchar(2000);
        logid                           bigint;
        _resultid                       promogest.resultid;
        TempId                          bigint;
    BEGIN
        IF _id IS NULL THEN
            INSERT INTO magazzino (denominazione,indirizzo,localita,cap,provincia,nazione,data_ultima_stampa_giornale) 
                VALUES (_denominazione,_indirizzo,_localita,_cap,_provincia,_nazione,_data_ultima_stampa_giornale);
                
            TempId := CURRVAL('magazzino_id_seq');
            PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.MagazzinoInsUpd','Inserito magazzino',NULL,TempId);
            SELECT INTO _resultid TempId;
        ELSE
            UPDATE magazzino SET
                 denominazione = COALESCE(_denominazione,denominazione)
                ,indirizzo = COALESCE(_indirizzo,indirizzo)
                ,localita = COALESCE(_localita,localita)
                ,cap = COALESCE(_cap,cap)
                ,provincia = COALESCE(_provincia,provincia)
                ,nazione = COALESCE(_nazione,nazione)
                ,data_ultima_stampa_giornale = COALESCE(_data_ultima_stampa_giornale,data_ultima_stampa_giornale)
            WHERE id = _id;

            IF FOUND THEN
                PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.MagazzinoInsUpd','Modificato magazzino',_denominazione,_id);
                SELECT INTO _resultid _id;
            ELSE
                PERFORM promogest.LogSet(_idutente, _schema,  'E','promogest.MagazzinoInsUpd','Magazzino non trovato',_denominazione,_id);
                RAISE WARNING 'Magazzino non trovato: %',_id;
                logid := CURRVAL('promogest.application_log_id_seq');
                SELECT INTO _resultid -logid;
            END IF;
        END IF;
        RETURN _resultid;
    END;
$_$
    LANGUAGE plpgsql;


