--

CREATE FUNCTION numeroregistroget(character varying, date) RETURNS SETOF numero_documento_type
    AS $_$

    DECLARE
        _type           ALIAS FOR $1;
        _date           ALIAS FOR $2;
        
        sql_command     varchar(2000);
        registro        varchar(100);
        rotazione       varchar(100);
        numero          integer;
        _key            varchar(1000);
        _currentyear    char(4);
        _rotation       varchar(100);
        v_row record;
    BEGIN
        _key := _type || '.registro';
        
        SELECT INTO registro value FROM setting WHERE key = _key;

        IF registro IS NULL THEN
            RAISE EXCEPTION 'Registro numerazione non trovato';
        END IF;
        
        _key := registro || '.rotazione';

        SELECT INTO rotazione value FROM setting WHERE key = _key;
        
        IF rotazione IS NULL THEN
            RAISE EXCEPTION 'Tipologia rotazione non trovata';
        END IF;

        SELECT INTO _rotation case rotazione WHEN 'annuale' THEN 'year' WHEN 'mensile' THEN 'month' WHEN 'giornaliera' THEN 'day' END;

        IF _type = 'movimento' THEN
            sql_command:= 'SELECT COALESCE(MAX(numero),0) + 1  AS numero, CAST(' || QUOTE_LITERAL(registro) || ' AS varchar(100)) AS registro FROM testata_movimento WHERE DATE_PART(''year'',data_movimento) = DATE_PART(' || QUOTE_LITERAL(_rotation) || ',CAST(' || QUOTE_LITERAL(_date) || 'AS DATE)) AND registro_numerazione = ' || QUOTE_LITERAL(registro);
        ELSE
            sql_command:= 'SELECT COALESCE(MAX(numero),0) + 1  AS numero, CAST(' || QUOTE_LITERAL(registro) || ' AS varchar(100)) AS registro FROM testata_documento WHERE DATE_PART(''year'',data_documento) = DATE_PART(' || QUOTE_LITERAL(_rotation) || ',CAST(' || QUOTE_LITERAL(_date) || 'AS DATE)) AND registro_numerazione = ' || QUOTE_LITERAL(registro);
        END IF;
        
        FOR v_row IN EXECUTE sql_command LOOP
            RETURN NEXT v_row;
        END LOOP;
        RETURN;
    END;
$_$
    LANGUAGE plpgsql;


