CREATE FUNCTION objectdel(character varying, bigint, character varying, bigint, character varying) RETURNS resultid
    AS $_$
    DECLARE
        -- Parametri contesto
        _schema         ALIAS FOR $1;
        _idutente       ALIAS FOR $2;

        _Object         ALIAS FOR $3;
        _Id             ALIAS FOR $4;
        _PK             ALIAS FOR $5;
        
        sql_statement   varchar(500);
        _row_status     bigint;
        logid           bigint;
        ret             promogest.resultid;
    BEGIN
        sql_statement:= ' DELETE FROM ' || _schema || '.' || _Object || ' WHERE ' || _PK || ' = ' || CAST(_Id AS varchar);
        EXECUTE sql_statement;
        GET DIAGNOSTICS _row_status = ROW_COUNT;
        IF _row_status > 0 THEN
            PERFORM promogest.LogSet(_idutente, _schema, 'I','ObjectDel','Deleted ' || _Object,NULL,_Id);
            SELECT INTO ret _Id;
        ELSE
            PERFORM promogest.LogSet(_idutente, _schema, 'E','ObjectDel','Object not found',NULL,_Id);
            RAISE WARNING 'Object not found: %',_Id;
            logid := CURRVAL('promogest.application_log_id_seq');
            SELECT INTO ret -logid;
        END IF;
        RETURN ret;
    END;
$_$
    LANGUAGE plpgsql;


