        logid                   bigint;
        _resultid               promogest.resultid;
        TempId                  bigint;
    BEGIN
        IF _id IS NULL THEN
            INSERT INTO promemoria (data_inserimento, data_scadenza, oggetto, incaricato, autore, descrizione, annotazione, riferimento, giorni_preavviso, in_scadenza, scaduto, completato)
                VALUES (CURRENT_TIMESTAMP, _data_scadenza, _oggetto, _incaricato, _autore, _descrizione, _annotazione, _riferimento, _giorni_preavviso, _in_scadenza, _scaduto, _completato);

            TempId := CURRVAL('promemoria_id_seq');
            PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.PromemoriaInsUpd','Inserito promemoria',NULL,TempId);
            SELECT INTO _resultid TempId;
        ELSE
            UPDATE promemoria SET
                 data_inserimento = COALESCE(_data_inserimento, data_inserimento)
                ,data_scadenza = COALESCE(_data_scadenza, data_scadenza)
                ,oggetto = COALESCE(_oggetto, oggetto)
