                ,giorni_preavviso = _giorni_preavviso
                ,in_scadenza = COALESCE(_in_scadenza, in_scadenza)
                ,scaduto = COALESCE(_scaduto, scaduto)
                ,completato = COALESCE(_completato, completato)
            WHERE id = _id;

            IF FOUND THEN
                PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.PromemoriaInsUpd','Modificato promemoria',NULL,_id);
                SELECT INTO _resultid _id;
