-- Name: scontoforniturainsupd(character varying, bigint, bigint, numeric, character varying, bigint); Type: FUNCTION; Schema: promogest; Owner: promoadmin
--

CREATE FUNCTION scontoforniturainsupd(character varying, bigint, bigint, numeric, character varying, bigint) RETURNS resultid
    AS $_$
    DECLARE
        -- Parametri contesto
        _schema             ALIAS FOR $1;
        _idutente           ALIAS FOR $2;
        
        -- Parametri tabella
        _id                 ALIAS FOR $3;
        _valore             ALIAS FOR $4;
        _tipo_sconto        ALIAS FOR $5;
        _id_fornitura       ALIAS FOR $6;
        sql_command         varchar(2000);
        schema_prec         varchar(2000);
        logid               bigint;
        _resultid           promogest.resultid;
        TempId              bigint;
    BEGIN
        IF _id IS NULL THEN
            INSERT INTO sconto (valore,tipo_sconto) 
                VALUES (_valore,_tipo_sconto);

            TempId := CURRVAL('sconto_id_seq');
                
            INSERT INTO sconto_fornitura (id, id_fornitura) 
                VALUES (TempId, _id_fornitura);
            
            PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.ScontoFornituraInsUpd','Inserito sconto fornitore',NULL,TempId);
            SELECT INTO _resultid TempId;
        ELSE
            UPDATE sconto SET
                 valore = COALESCE(_valore,valore)
                ,tipo_sconto = COALESCE(_tipo_sconto,tipo_sconto)
            WHERE id = _id;

            UPDATE sconto_fornitura SET
                 id_fornitura = _id_fornitura
            WHERE id = _id;

            IF FOUND THEN
                PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.ScontoFornituraInsUpd','Modificato sconto fornitore',NULL,_id);
                SELECT INTO _resultid _id;
            ELSE
                PERFORM promogest.LogSet(_idutente, _schema,  'E','promogest.ScontoFornituraInsUpd','Sconto fornitore non trovato',NULL,_id);
                RAISE WARNING 'Sconto fornitore non trovato: %', _id;
                logid := CURRVAL('promogest.application_log_id_seq');
                SELECT INTO _resultid -logid;
            END IF;
        END IF; 
        RETURN _resultid;
    END;
$_$
    LANGUAGE plpgsql;


