        _resultid               promogest.resultid;
        TempId                  bigint;
    BEGIN
        IF _id IS NULL THEN
            INSERT INTO testata_movimento (numero,parte,registro_numerazione,operazione,data_movimento,data_inserimento,note_interne,note_pie_pagina,id_testata_documento,id_cliente,id_fornitore) 
                VALUES (_numero,_parte,_registro_numerazione,_operazione,_data_movimento,CURRENT_TIMESTAMP,_note_interne,_note_pie_pagina,_id_testata_documento,_id_cliente,_id_fornitore);

            TempId := CURRVAL('testata_movimento_id_seq');
            PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.TestataMovimentoInsUpd','Inserita testata movimento',NULL,TempId);
            SELECT INTO _resultid TempId;
        ELSE
            UPDATE testata_movimento SET
                 numero = COALESCE(_numero,numero)
                ,parte = COALESCE(_parte,parte)
                ,registro_numerazione = COALESCE(_registro_numerazione,registro_numerazione)
