                ,note_pie_pagina = _note_pie_pagina
                ,id_testata_documento = _id_testata_documento
                ,id_cliente = _id_cliente
                ,id_fornitore = _id_fornitore
            WHERE id = _id;

            IF FOUND THEN
                PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.TestataMovimentoInsUpd','Modificata testata movimento',NULL,_id);
