-- Name: testatascontrinoinsupd(character varying, bigint, bigint, numeric, numeric, numeric, numeric, bigint); Type: FUNCTION; Schema: promogest; Owner: promoadmin
--

CREATE FUNCTION testatascontrinoinsupd(character varying, bigint, bigint, numeric, numeric, numeric, numeric, bigint) RETURNS resultid
    AS $_$

    DECLARE
        -- Parametri contesto
        _schema                     ALIAS FOR $1;
        _idutente                   ALIAS FOR $2;

        -- Parametri tabella
        _id                         ALIAS FOR $3;
        _totale_scontrino           ALIAS FOR $4;
        _totale_contanti            ALIAS FOR $5;
        _totale_assegni             ALIAS FOR $6;
        _totale_carta_credito       ALIAS FOR $7;
        _id_testata_movimento       ALIAS FOR $8;

        schema_prec                 varchar(2000);
        logid                       bigint;
        _resultid                   promogest.resultid;
        TempId                      bigint;
    BEGIN
        IF _id IS NULL THEN
            INSERT INTO testata_scontrino(totale_scontrino, totale_contanti, totale_assegni, totale_carta_credito, id_testata_movimento)
                VALUES (_totale_scontrino, _totale_contanti, _totale_assegni, _totale_carta_credito, _id_testata_movimento);

            TempId := CURRVAL('testata_scontrino_id_seq');
            PERFORM promogest.LogSet(_idutente, _schema, 'I', 'promogest.TestataScontrinoInsUpd', 'Inserita testata scontrino', NULL, TempId);
            SELECT INTO _resultid TempId;
        ELSE
            UPDATE testata_scontrino SET
                 totale_scontrino = COALESCE(_totale_scontrino, totale_scontrino)
                ,totale_contanti = _totale_contanti
                ,totale_assegni = _totale_assegni
                ,totale_carta_credito = _totale_carta_credito
                ,id_testata_movimento = COALESCE(id_testata_movimento, _id_testata_movimento)
            WHERE id = _id;

            IF FOUND THEN
                PERFORM promogest.LogSet(_idutente, _schema, 'I', 'promogest.TestataScontrinoInsUpd', 'Modificata testata scontrino', NULL, _id);
                SELECT INTO _resultid _id;
            ELSE
                PERFORM promogest.LogSet(_idutente, _schema, 'E', 'promogest.TestataScontrinoInsUpd', 'Testata scontrino non trovata', NULL, _id);
                RAISE WARNING 'Testata documento non trovata: %', _id;
                logid := CURRVAL('promogest.application_log_id_seq');
                SELECT INTO _resultid -logid;
            END IF;
        END IF;
        RETURN _resultid;
    END;
$_$
    LANGUAGE plpgsql;


