--

CREATE FUNCTION unitabaseinsupd(character varying, bigint, bigint, character varying, character varying) RETURNS resultid
    AS $_$
    DECLARE
        -- Parametri contesto
        _schema                     ALIAS FOR $1;
        _idutente                   ALIAS FOR $2;

        --   Parametri tabella
        _id                         ALIAS FOR $3;
        _denominazione_breve        ALIAS FOR $4;
        _denominazione              ALIAS FOR $5;
        sql_command                 varchar(2000);
        schema_prec                 varchar(2000);
        logid                       bigint;
        _resultid                   promogest.resultid;
        TempId                      bigint;
    BEGIN
        IF _id IS NULL THEN
            INSERT INTO unita_base (denominazione_breve, denominazione) 
                VALUES (_denominazione_breve, denominazione);
                
            TempId := CURRVAL('unita_base_id_seq');
            PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.UnitaBaseInsUpd','Inserita unita base',NULL,TempId);
            SELECT INTO _resultid TempId;
        ELSE
            UPDATE unita_base SET
                 denominazione_breve = COALESCE(_denominazione_breve,denominazione_breve)
                ,denominazione = COALESCE(_denominazione,denominazione)
            WHERE id = _id;

            IF FOUND THEN
                PERFORM promogest.LogSet(_idutente, _schema, 'I','promogest.UnitaBaseInsUpd','Modificata unita base',NULL,_id);
                SELECT INTO _resultid _id;
            ELSE
                PERFORM promogest.LogSet(_idutente, _schema,  'E','promogest.UnitaBaseInsUpd','Unita base non trovata',NULL,_id);
                RAISE WARNING 'UnitaBase non trovata: %',_id;
                logid := CURRVAL('promogest.application_log_id_seq');
                SELECT INTO _resultid -logid;
            END IF;
        END IF; 
        RETURN _resultid;
    END;
$_$
    LANGUAGE plpgsql;


